function editStudentSchedule(id) {
    $("#updateOR").show();
    $("#or_number").prop("disabled", false);
    $("#or_date").prop("disabled", false);

    $.ajax({
        method: 'POST',
        url: '../app/includes/getStudent.inc.php',
        data: {
            api_key: "tPmAT5Ab3j7F9",
            id: id
        }
    })
        .done(function (response) {
            // console.log(response);

            var responseData = JSON.parse(response);
            // console.log(responseData)
            var scheduleData = responseData.data[0];
            // console.log(scheduleData)

            if (scheduleData !== undefined) {
                var full_name = scheduleData.last_name + ", " + scheduleData.first_name + " " + scheduleData.middle_name;
                $("#student_id").val(scheduleData.id);
                $("#student_code").val(scheduleData.student_code);
                $("#campus").val(scheduleData.campus);
                $("#name").val(full_name);
                $("#grade").val(scheduleData.grade);
                $("#old_exam_schedule_id").val(scheduleData.exam_schedule_id);
                $("#old_seat_number").val(scheduleData.seat_number);
                $("#or_number").val(scheduleData.or_number);
                $("#or_date").val(scheduleData.or_date);

                if (scheduleData.exam_schedule_id > 0) {
                    $("#saveStudentSchedule").hide();
                    $('#updateStudentSchedule').show();
                    $("#updateOR").show();
                    $("#rescheduleStudentSchedule").show();
                } else {
                    $("#saveStudentSchedule").show();
                    $('#updateStudentSchedule').hide();
                    $("#updateOR").hide();
                }
                populateSchedule();
            } else {
                swal("No Room Avaliable For " + exam_class);
            }
        })
        .fail(function (jqXHR, textStatus, errorThrown) {
            console.log(errorThrown);
        });
}

