function populateSchedule() {
    grade = $("#grade").val();
    campus = $("#campus").val();
    
    if (grade > 100) {
        exam_class = "INVALID GRADE";
    } else if (grade >= 90) {
        exam_class = "A";
    } else if (grade >= 85) {
        exam_class = "B";
    } else if (grade >= 81) {
        exam_class = "C";
    } else {
        exam_class = "INVALID GRADE";
    }
    // alert(exam_class)

    if (exam_class != "INVALID GRADE") {
        $.ajax({
            method: 'POST',
            url: '../app/includes/getAvailableExamSchedule.inc.php',
            data: {
                api_key: "tPmAT5Ab3j7F9",
                exam_class: exam_class,
                campus: campus
            }
        })
            .done(function (response) {
                // console.log(response)

                var responseData = JSON.parse(response);
                // console.log(responseData)
                var examData = responseData.data[0];
                // console.log(examData)

                if (examData !== undefined) {
                    seat = (examData.seat_number !== undefined) ? examData.seat_number : +examData.room_counter + 1;
                    from_available = examData.seat_number !== undefined;
                    $("#from_available").val(from_available);
                    $("#seat_number").val(seat);
                    $("#exam_schedule_id").val(examData.id);
                    $("#class").val(examData.class);
                    $("#exam_date").val(examData.exam_date);
                    $("#exam_time").val(examData.exam_time);
                    $("#room_number").val(examData.room_number);
                } else {
                    $("#exam_schedule_id").val("");
                    $("#class").val("");
                    $("#exam_date").val("");
                    $("#exam_time").val("");
                    $("#room_number").val("");
                    $("#seat_number").val("");
                    swal("No Room Avaliable For " + exam_class);
                }
            })
            .fail(function (jqXHR, textStatus, errorThrown) {
                console.log(errorThrown);
            });
    } else {
        swal("Invalid Grade!", "", "warning");
        $("#saveStudentSchedule").prop("disabled", true);
        $("#updateStudentSchedule").prop("disabled", true);
        $("#updateOR").prop("disabled", true);
    }
}

function populateStudentInformation() {
    code = $("#code").val();
    if (code !== "") {
        $.ajax({
            method: 'POST',
            url: '../app/includes/getStudentInformation.inc.php',
            data: {
                api_key: "tPmAT5Ab3j7F9",
                appointment_code: code
            }
        })
            .done(function (response) {
                // console.log(response);

                var responseData = JSON.parse(response);
                // console.log(responseData)
                var studentData = responseData.data[0];
                // console.log(studentData)

                if (studentData !== undefined) {
                    date = new Date(studentData.date_sched);
                    hours = (date.getHours() <= 12) ? `${date.getHours()}:00 AM` : `${date.getHours() - 12}:00 PM`;
                    $("#appointment_date").val(`${date.getMonth() + 1} - ${date.getDate()} - ${date.getFullYear()}`);
                    $("#appointment_time").val(hours);
                    $("#id").val(studentData.id);
                    $("#lastname").val(studentData.last_name);
                    $("#firstname").val(studentData.first_name);
                    $("#middlename").val(studentData.middle_name);
                    $("#pob").val(studentData.pob);
                    $("#dob").val(studentData.dob);
                    $("#gender").val(studentData.gender);
                    $("#age").val(studentData.age);
                    $(`input[name='civil_status'][value='${studentData.civil_status}']`).prop("checked", true);
                    $("#citizenship").val(studentData.citizenship);
                    $("#religion").val(studentData.religion);
                    $("#email").val(studentData.email);
                    $("#phone").val(studentData.phone);
                    $("#telephone").val(studentData.telephone);
                    $("#address").val(studentData.address);
                    $("#brgy").val(studentData.brgy);
                    $("#zone").val(studentData.zone);
                    $("#district").val(studentData.district);
                    $("#mother_name").val(studentData.mother_name);
                    $("#father_name").val(studentData.father_name);
                    $("#mother_occupation").val(studentData.mother_occupation);
                    $("#father_occupation").val(studentData.father_occupation);
                    $("#guardian").val(studentData.guardian);
                    $("#guardian_relation").val(studentData.guardian_relation);
                    $("#guardian_address").val(studentData.guardian_address);
                    $("#elem_school").val(studentData.elementary);
                    $("#elem_school_graduated").val(String(studentData.elementary_graduated));
                    $("#high_school").val(studentData.high_school);
                    $("#high_school_graduated").val(String(studentData.high_school_graduated));
                    $("#senior_high").val(studentData.senior_high);
                    $("#senior_high_graduated").val(String(studentData.senior_high_graduated));
                    $("#strand").val(studentData.strand);

                    if (studentData.grade !== undefined) {
                        $("#grade").val(studentData.grade);
                        $("#birth_certificate").prop("checked", (studentData.birth_certificate == 1) ? true : false);
                        $("#diploma_elementary").prop("checked", (studentData.diploma_elementary == 1) ? true : false);
                        $("#diploma_high_school").prop("checked", (studentData.diploma_high_school == 1) ? true : false);
                        $("#voters_id_parent").prop("checked", (studentData.voters_id_parent == 1) ? true : false);
                        $("#voters_id_applicant").prop("checked", (studentData.voters_id_applicant == 1) ? true : false);
                        $("#voters_certificate_parent").prop("checked", (studentData.voters_certificate_parent == 1) ? true : false);
                        $("#voters_certificate_applicant").prop("checked", (studentData.voters_certificate_applicant == 1) ? true : false);
                        $("#good_moral").prop("checked", (studentData.good_moral == 1) ? true : false);
                        $("#form_138").prop("checked", (studentData.form_138 == 1) ? true : false);
                    } else {
                        $("#grade").val("");
                        $("#birth_certificate").prop("checked", false);
                        $("#diploma_elementary").prop("checked", false);
                        $("#diploma_high_school").prop("checked", false);
                        $("#voters_id_parent").prop("checked", false);
                        $("#voters_id_applicant").prop("checked", false);
                        $("#voters_certificate_parent").prop("checked", false);
                        $("#voters_certificate_applicant").prop("checked", false);
                        $("#good_moral").prop("checked", false);
                        $("#form_138").prop("checked", false);
                    }
                } else {
                    swal("No Student Record Found", "", "warning");
                    clearForm();
                }
            })
            .fail(function (jqXHR, textStatus, errorThrown) {
                console.log(errorThrown);
            });
    } else {
        swal("No Student Record Found", "", "warning");
    }
}

